    useEffect(() => { 
        const newList = expenses.filter((ex) => ex.get)
       setFilteredExpenses() 
    },[selectedYear])
    return (
        <div>
          <ExpensesFilter/>
            <div className='expenses'>
                {expenses.map((expense) => (
                 <ExpenseItem { ...expense } />   
                ))}
          </div>
        </div>
    )

}
